import React, { Component } from "react";
import { AvForm, AvField } from "availity-reactstrap-validation";
import axios from "axios";

class Inscription extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      password: ""
    };
  }

  async submitForm(e) {
    e.preventDefault();
    const { name, password } = this.state;
    await axios
      .post("/alohomora/inscription", {
        name,
        password
      })
      .then(res => {
        if (res.error) {
          alert(res.error);
        }
      })
      .catch(e => {
        console.error(e);
        alert("ajout d'un ingrédient");
      });
  }

  render() {
    return (
      <AvForm className="Form_Connexion" onSubmit={this.submitForm}>
        <AvField
          name="nameCustomMessage"
          type="name"
          label="Pseudonyme :"
          validate={{
            required: { value: true, errorMessage: "Entrez un pseudonyme ici" },
            pattern: {
              value: "^[A-Za-z0-9]+$",
              errorMessage:
                "Votre nom ne peut contenir que des lettres et des chiffres"
            },
            minLength: {
              value: 4,
              errorMessage: "Votre nom doit contenir au minimum 4 caractères"
            },
            maxLength: {
              value: 16,
              errorMessage: "Votre nom ne peut excéder 16 caractères"
            }
          }}
        />

        <AvField
          name="passwordCustomMessage"
          type="password"
          label="Mot de passe :"
          validate={{
            required: {
              value: true,
              errorMessage: "Entrez un mot de passe ici"
            },
            pattern: {
              value: "^[A-Za-z0-9]+$",
              errorMessage:
                "Votre mot de passe ne peut contenir que des lettres et des chiffres"
            },
            minLength: {
              value: 8,
              errorMessage:
                "Votre mot de passe doit contenir au minimum 8 caractères"
            },
            maxLength: {
              value: 30,
              errorMessage: "Votre mot de passe ne peut excéder 30 caractères"
            }
          }}
        />
        <button color="primary">Valider</button>
      </AvForm>
    );
  }
}

export default Inscription;
