import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import axios from "axios";
import "./oeuvres.css";
import Nav from "../Acceuil/Nav";
import Footer from "../Acceuil/Footer";
import useState from "react";

class Oeuvres extends Component {
  constructor(props) {
    super(props);
    this.state = {
      oeuvres: []
    };
  }

  componentDidMount() {
    axios
      .get("/alohomora/oeuvres")
      .then(res => res.data)
      .then(response => {
        this.setState({
          oeuvres: response
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const ModalOeuvre = props => {
      const { buttonLabel, className } = props;

      const [modal, setModal] = useState(false);
      const toggle = () => setModal(!modal);
      const { oeuvres } = this.state;
      return (
        <div className="oeuvres-grid">
          <Nav />
          <h1 className="oeuvres-titre">Livres</h1>
          <div className="oeuvres-contain">
            {oeuvres.map((oeuvre, id) => (
              <div className="button-end">
                <div className="oeuvres-card" key={id}>
                  <img
                    src={oeuvre.image}
                    alt={oeuvre.nom}
                    className="oeuvres-image"
                  />
                  <h2 className="oeuvres-card-titre">{oeuvre.nom}</h2>
                  <p className="oeuvres-card-soustitre">{oeuvre.autre}</p>
                  <p className="oeuvres-card-resume">{oeuvre.résumé}</p>
                  <div>
                    <Button
                      className="oeuvres-button"
                      color="danger"
                      onClick={toggle}
                    >
                      {buttonLabel}
                    </Button>
                    <Modal
                      key={id}
                      isOpen={modal}
                      fade={false}
                      toggle={toggle}
                      className={className}
                    >
                      <ModalHeader toggle={toggle}>Modal title</ModalHeader>
                      <ModalBody>
                        Lorem ipsum dolor sit amet, consectetur adipisicing
                        elit, sed do eiusmod tempor incididunt ut labore et
                        dolore magna aliqua. Ut enim ad minim veniam, quis
                        nostrud exercitation ullamco laboris nisi ut aliquip ex
                        ea commodo consequat. Duis aute irure dolor in
                        reprehenderit in voluptate velit esse cillum dolore eu
                        fugiat nulla pariatur. Excepteur sint occaecat cupidatat
                        non proident, sunt in culpa qui officia deserunt mollit
                        anim id est laborum.
                      </ModalBody>
                      <ModalFooter>
                        <Button color="secondary" onClick={toggle}>
                          Cancel
                        </Button>
                      </ModalFooter>
                    </Modal>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <Footer />
        </div>
      );
    };
    return <ModalOeuvre />;
  }
}

export default Oeuvres;
