import React, { Component } from "react";
import axios from "axios";
import "./commentaires.css";

class Commentaires extends Component {
  constructor(props) {
    super(props);
    this.state = {
      comments: [],
      name: "",
      texte: ""
    };
    this.onChange = this.onChange.bind(this);
  }

  componentDidMount() {
    axios
      .get("/alohomora/commentaires")
      .then(response => response.data)
      .then(response => {
        this.setState({
          comments: response
        });
      });
  }

  postComment(e) {
    e.preventDefault();
    const { texte, name } = this.state;
    axios.post("/alohomora/commentaires", {
      texte,
      name
    });
  }

  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  render() {
    const { comments } = this.state;
    return (
      <div>
        <h1 className="comment-titre">Commentaires</h1>
        <div className="comments-contain">
          {comments.map((comment, id) => (
            <div className="comment-contain" key={id}>
              <h1 className="comment-nom"> De: {comment.name}</h1>
              <p className="comment-message">{comment.texte}</p>
            </div>
          ))}
          <div className="comments-post">
            <form className="comment-post">
              <label>Nom :</label>
              <input
                type="text"
                name="name"
                value={this.state.name}
                onChange={this.onChange}
              />
              <label>Commentaire :</label>
              <input
                type="textarea"
                name="texte"
                value={this.state.texte}
                className="comment-text"
                onChange={this.onChange}
              />
              <button type="submit" className="comment-button">
                Envoyer
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Commentaires;
