import React, { Component } from "react";
import axios from "axios";
import "./oeuvres.css";
import Nav from "../Acceuil/Nav";
import Footer from "../Acceuil/Footer";
import {
  Card,
  Button,
  CardImg,
  CardTitle,
  CardText,
  CardGroup,
  CardSubtitle,
  CardBody
} from "reactstrap";

class Oeuvres extends Component {
  constructor(props) {
    super(props);
    this.state = {
      oeuvres: []
    };
  }

  componentDidMount() {
    axios
      .get("/alohomora/oeuvres")
      .then(res => res.data)
      .then(response => {
        this.setState({
          oeuvres: response
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { oeuvres } = this.state;
    return (
      <div className="oeuvres-grid">
        <Nav />
        <div className="oeuvres-livres">
          <h1 className="oeuvres-titre">Livres</h1>
          <CardGroup className="oeuvres-contain">
            {oeuvres.map((oeuvre, id) => (
              <Card className="oeuvres-card" key={id}>
                <CardImg
                  top
                  width="100%"
                  src={oeuvre.image}
                  alt={oeuvre.nom}
                  className="oeuvres-image"
                />
                <CardBody>
                  <CardTitle>{oeuvre.nom}</CardTitle>
                  <CardSubtitle>{oeuvre.autre}</CardSubtitle>
                  <CardText>{oeuvre.résumé}</CardText>
                  <Button>Clique pas y'a R</Button>
                </CardBody>
              </Card>
            ))}
          </CardGroup>
        </div>
        <Footer />
      </div>
    );
  }
}

export default Oeuvres;
