import React, { Component } from "react";
import axios from "axios";
import "./oeuvres.css";
import Nav from "../Acceuil/Nav";
import Footer from "../Acceuil/Footer";
import Modal from "react-modal";

Modal.setAppElement("#Oeuvres");

class Oeuvres extends Component {
  constructor(props) {
    super(props);
    this.state = {
      oeuvres: [],
      modalIsOpen: false,
      content: {
        top: "50%",
        left: "50%",
        right: "auto",
        bottom: "auto",
        marginRight: "-50%",
        transform: "translate(-50%, -50%)"
      }
    };
    this.openModal = this.openModal.bind(this);
    // this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
  }

  openModal() {
    this.setState({ modalIsOpen: true });
  }

  // afterOpenModal() {
  //   this.subtitle.style.color = "#f00";
  // }

  closeModal() {
    this.setState({ modalIsOpen: false });
  }

  componentDidMount() {
    axios
      .get("/alohomora/oeuvres")
      .then(res => res.data)
      .then(response => {
        this.setState({
          oeuvres: response
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { oeuvres } = this.state;
    return (
      <div className="oeuvres-grid">
        <Nav />
        <h1 className="oeuvres-titre">Livres</h1>
        <div className="oeuvres-contain">
          {oeuvres.map((oeuvre, id) => (
            <div className="button-end">
              <div className="oeuvres-card" key={id}>
                <img
                  src={oeuvre.image}
                  alt={oeuvre.nom}
                  className="oeuvres-image"
                />
                <h2 className="oeuvres-card-titre">{oeuvre.nom}</h2>
                <p className="oeuvres-card-soustitre">{oeuvre.autre}</p>
                <p className="oeuvres-card-resume">{oeuvre.résumé}</p>

                <button className="oeuvres-button" onClick={this.openModal}>
                  Clique pas y'a R
                </button>

                <Modal
                  key={id}
                  isOpen={this.state.modalIsOpen}
                  onAfterOpen={this.afterOpenModal}
                  onRequestClose={this.closeModal}
                  style={this.content}
                  contentLabel="Example Modal"
                >
                  <h2>{oeuvre.nom}</h2>
                  <button onClick={this.closeModal}>close</button>
                  <div>{oeuvre.résumé}</div>
                </Modal>
              </div>
            </div>
          ))}
        </div>
        <Footer />
      </div>
    );
  }
}

export default Oeuvres;
