import React, { Component } from "react";
import Nav from "../Acceuil/Nav";
import axios from "axios";
import Footer from "../Acceuil/Footer";
import "./personnages.css";

class Personnages extends Component {
  constructor(props) {
    super(props);
    this.state = {
      personnages: [],
      filteredByHouse: "None",
      filteredByType: "None",
      filteredBySide: "None"
    };
  }

  componentDidMount() {
    axios
      .get("/alohomora/personnages")
      .then(res => res.data)
      .then(response => {
        this.setState({
          personnages: response
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const {
      activeMaisonFilter,
      activeTypeFilter,
      activeSideFilter
    } = this.props;
    const { personnages } = this.state;
    const filteredPersonnages = personnages
      .filter(
        recette =>
          !activeMaisonFilter || recette.ingredient_id === +activeMaisonFilter
      )
      .filter(
        recette => !activeTypeFilter || recette.texture_id === activeTypeFilter
      )
      .filter(
        recette =>
          !activeSideFilter ||
          (recette.nb_etape <= +activeSideFilter &&
            recette.nb_etape >= +activeStepFilter - 2)
      );
    return (
      <div className="persos-contain">
        <div className="persos">
          {filteredPersonnages.map((personnage, id) => (
            <div className="perso-card" key={id}>
              <img
                src={personnage.image}
                alt={personnage.nom}
                className="persos-image"
              />
              <h1>{personnage.nom}</h1>
              <p>
                <strong>Type: </strong>
                {personnage.type}
              </p>
              <p>
                <strong>Côté: </strong>
                {personnage.side}
              </p>
              <p>
                <strong>Maison: </strong>
                {personnage.maison}
              </p>
              <p>
                <strong>Acteur: </strong>
                {personnage.acteur}
              </p>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default Personnages;
