import React, { Component } from "react";
import axios from "axios";

const path = "../../../../back/public/";

class Oeuvres extends Component {
  constructor(props) {
    super(props);
    this.state = {
      oeuvres: [],
      images: []
    };
  }

  componentDidMount() {
    axios
      .get("/alohomora/oeuvres")
      .then(res => res.data)
      .then(response => {
        this.setState({
          oeuvres: response
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const newImages = oeuvres.map(path + oeuvres.image);
    oeuvres.push(newImages);
    return (
      <div className="oeuvres-grid">
        <h1 className="oeuvres-titre">Livres</h1>
        <div className="oeuvres-livres">
          {oeuvres.map((oeuvre, id) => (
            <div className="oeuvres-card" key={id}>
              <img src={newImages.oeuvre.image} alt={oeuvre.name} />
              <h1>{oeuvre.name}</h1>
              <p>{oeuvre.description}</p>
              <p>{oeuvre.autre}</p>
              <button>Clique pas y'a R</button>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default Oeuvres;
