import React, { Component } from "react";
import axios from "axios";

class Commentaires extends Component {
  constructor(props) {
    super(props);
    this.state = {
      comments: [],
      oeuvres_id: null,
      users_id: null,
      texte: ""
    };
  }

  componentDidMount() {
    axios
      .get("/alohomora/commentaires")
      .then(response => response.data)
      .then(response => {
        this.setState({
          comments: response
        });
      });
  }

  postComment() {
    const { texte, oeuvres_id, users_id } = this.state;
    axios.post("/alohomora/commentaires",
      {
        texte,
        oeuvres_id,
        users_id
      })
      .then(res => {
        if (res.error) {
          alert(res.error);
        }
      })
      .catch(e => {
        console.error(e);
        alert("nope");
      });
  }

  render() {
    const { comments } = this.state;
    return (
      <div className="comments-contain">
        {comments.map((comment, id) => (
          <div className="comment-contain" key={id}>
            <h1>{comment.name}</h1>
            <p>{comment.texte}</p>
          </div>
        ))}
        <div className="comments-post">
          <form>
            <input type="textarea" label="Commentaire :" />
            <button>Envoyer</button>
          </form>
        </div>
      </div>
    );
  }
}

export default Commentaires;
