import React, { Component } from "react";
import { AvForm, AvField } from "availity-reactstrap-validation";
import { Button } from "reactstrap";

class Inscription extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      password: ""
    };
  }

  render() {
    return (
      <AvForm className="Form_Connexion">
        <AvField
          name="name"
          label="Name (default error message)"
          type="text"
          errorMessage="Invalid name"
          validate={{
            required: { value: true },
            pattern: { value: "^[A-Za-z0-9]+$" },
            minLength: { value: 6 },
            maxLength: { value: 16 }
          }}
        />
        <AvField
          name="nameCustomMessage"
          label="Name (custom error message)"
          type="text"
          validate={{
            required: { value: true, errorMessage: "Entrez un pseudonyme ici" },
            pattern: {
              value: "^[A-Za-z0-9]+$",
              errorMessage:
                "Votre nom ne peut contenir que des lettres et des chiffres"
            },
            minLength: {
              value: 4,
              errorMessage: "Votre nom doit contenir au minimum 4 caractères"
            },
            maxLength: {
              value: 16,
              errorMessage: "Votre nom ne peut excéder 16 caractères"
            }
          }}
        />
        <Button color="primary">Valider</Button>
      </AvForm>
    );
  }
}

export default Inscription;
